<div class="layui-card layui-bg-gray"><?php if(!(empty($title) || (($title instanceof \think\Collection || $title instanceof \think\Paginator ) && $title->isEmpty()))): ?><div class="layui-card-header layui-anim layui-anim-fadein notselect"><span class="layui-icon layui-icon-next font-s10 color-desc margin-right-5"></span><?php echo htmlentities((isset($title) && ($title !== '')?$title:'')); ?><div class="pull-right"></div></div><?php endif; ?><div class="layui-card-body layui-anim layui-anim-upbit"><div class="think-box-shadow"><fieldset><legend>条件搜索</legend><form class="layui-form layui-form-pane form-search" action="<?php echo request()->url(); ?>" onsubmit="return false" method="get" autocomplete="off"><div class="layui-form-item layui-inline"><label class="layui-form-label">用户名</label><div class="layui-input-inline"><input name="phone" value="<?php echo htmlentities((app('request')->get('phone') ?: '')); ?>" placeholder="请输入用户名" class="layui-input"></div></div><div class="layui-form-item layui-inline"><label class="layui-form-label">查看类型</label><div class="layui-input-inline"><select class="layui-select" name="type"><?php foreach(['1'=>'查看上级','2'=>'查看下级'] as $k=>$v): ?><!--<?php if(app('request')->get('type') == $k.""): ?>--><option selected value="<?php echo htmlentities($k); ?>"><?php echo htmlentities($v); ?></option><!--<?php else: ?>--><option value="<?php echo htmlentities($k); ?>"><?php echo htmlentities($v); ?></option><!--<?php endif; ?>--><?php endforeach; ?></select></div></div><div class="layui-form-item layui-inline"><button class="layui-btn layui-btn-primary"><i class="layui-icon">&#xe615;</i> 搜 索</button></div></form><script>form.render()</script></fieldset><table class="layui-table margin-top-10" lay-skin="line"><?php if(!(empty($list) || (($list instanceof \think\Collection || $list instanceof \think\Paginator ) && $list->isEmpty()))): ?><thead><tr><th class='text-center nowrap'>会员ID</th><th class="text-center nowrap">用户名</th><th class="text-center nowrap">姓名</th><th class="text-center nowrap">余额</th><th class="text-center nowrap">是否认证</th><th class="text-center nowrap">推荐人用户名</th><th class='text-center nowrap'>编号</th></tr></thead><tbody><?php foreach($list as $key=>$vo): ?><tr><td class='text-center nowrap'><?php echo htmlentities((isset($vo['id']) && ($vo['id'] !== '')?$vo['id']:'--')); ?></td><td class='text-center nowrap'><?php echo htmlentities((isset($vo['phone']) && ($vo['phone'] !== '')?$vo['phone']:'--')); ?></td><td class='text-center nowrap'><?php echo htmlentities((isset($vo['name']) && ($vo['name'] !== '')?$vo['name']:'--')); ?></td><td class='text-center nowrap'><?php echo htmlentities((isset($vo['money']) && ($vo['money'] !== '')?$vo['money']:'--')); ?></td><td class='text-center nowrap'><?php if($vo['auth'] == 1): ?><span class="color-green">已认证</span><?php endif; if($vo['auth'] == 0): ?><span class="color-red">未认证</span><?php endif; ?></td><td class='text-center nowrap'><?php echo htmlentities((isset($vo['top_phone']) && ($vo['top_phone'] !== '')?$vo['top_phone']:'--')); ?></td><td class='text-center nowrap'><a data-title="查看" class="layui-btn layui-btn-sm layui-btn-warm" data-open='<?php echo url("akszadmin/users/index"); ?>?u_phone=<?php echo htmlentities($vo['phone']); ?>'>查看详情</a></td></tr><?php endforeach; ?><?php endif; ?></tbody></table><?php if(empty($list) || (($list instanceof \think\Collection || $list instanceof \think\Paginator ) && $list->isEmpty())): ?><span class="notdata">没有记录哦</span><?php else: ?><?php echo (isset($pagehtml) && ($pagehtml !== '')?$pagehtml:''); ?><?php endif; ?></div></div></div>