<div class="layui-card layui-bg-gray"><?php if(!(empty($title) || (($title instanceof \think\Collection || $title instanceof \think\Paginator ) && $title->isEmpty()))): ?><div class="layui-card-header layui-anim layui-anim-fadein notselect"><span class="layui-icon layui-icon-next font-s10 color-desc margin-right-5"></span><?php echo htmlentities((isset($title) && ($title !== '')?$title:'')); ?><div class="pull-right"></div></div><?php endif; ?><div class="layui-card-body layui-anim layui-anim-upbit"><form onsubmit="return false;" id="GoodsForm" data-auto="true" method="post" class='layui-form layui-card' autocomplete="off"><div class="layui-card-body think-box-shadow padding-left-40"><div class="layui-form-item layui-row layui-col-space15"><label class="layui-col-xs3 relative"><span class="color-green">文章分类</span><select class="layui-select" required name="type" lay-search><?php foreach($class as $cate): if(isset($vo['type']) and $vo['type'] == $cate['id']): ?><option selected value="<?php echo htmlentities($cate['id']); ?>"><?php echo htmlentities((isset($cate['name']) && ($cate['name'] !== '')?$cate['name']:'')); ?></option><?php else: ?><option value="<?php echo htmlentities($cate['id']); ?>"><?php echo htmlentities((isset($cate['name']) && ($cate['name'] !== '')?$cate['name']:'')); ?></option><?php endif; ?><?php endforeach; ?></select></label><label class="layui-col-xs9 relative"><span class="color-green">文章名称</span><input name="title" required class="layui-input" placeholder="请输入文章名称" value="<?php echo htmlentities((isset($vo['title']) && ($vo['title'] !== '')?$vo['title']:'')); ?>"></label></div><div class="layui-form-item layui-row layui-col-space15"><label class="layui-col-xs3 relative"><span class="color-green">是否显示</span><select class="layui-select" name="show"><?php foreach(['1'=>'显示','0'=>'隐藏'] as $k=>$v): ?><!--<?php if($vo['show'] == $k.""): ?>--><option selected value="<?php echo htmlentities($k); ?>"><?php echo htmlentities($v); ?></option><!--<?php else: ?>--><option value="<?php echo htmlentities($k); ?>"><?php echo htmlentities($v); ?></option><!--<?php endif; ?>--><?php endforeach; ?></select></label><label class="layui-col-xs9 relative"><span class="color-green">关键词</span><input name="keyword" class="layui-input" placeholder="请输入文章关键词" value="<?php echo htmlentities((isset($vo['keyword']) && ($vo['keyword'] !== '')?$vo['keyword']:'')); ?>"></label></div><div class="layui-form-item block"><span class="label-required-prev color-green">文章内容</span><textarea name="content"><?php echo (isset($vo['content']) && ($vo['content'] !== '')?$vo['content']:''); ?></textarea></div><div class="layui-form-item text-center"><?php if(!(empty($vo['id']) || (($vo['id'] instanceof \think\Collection || $vo['id'] instanceof \think\Paginator ) && $vo['id']->isEmpty()))): ?><input type="hidden" name="id" value="<?php echo htmlentities($vo['id']); ?>"><?php endif; ?><button class="layui-btn layui-btn-danger" ng-click="hsitoryBack()" type="button">取消编辑</button><button class="layui-btn" type="submit">保存文章</button></div></div></form></div><script>    window.form.render();
    require(['ckeditor', 'angular'], function () {
        window.createEditor('[name="content"]', {height: 500});
        var app = angular.module("GoodsForm", []).run(callback);
        angular.bootstrap(document.getElementById(app.name), [app.name]);

        function callback($rootScope) {
            $rootScope.hsitoryBack = function () {
                $.msg.confirm('确定要取消编辑吗？', function (index) {
                    history.back(), $.msg.close(index);
                });
            };
        }

    })
</script></div>