ities($vo['id']); ?>;do#<?php echo $vo['showps']==1 ? 0 : 1; ?>" data-csrf="<?php echo systoken('showps'); ?>"><?php echo $vo['showps']==1 ? "隐盈" : "显盈"; ?></a><a class="layui-btn layui-btn-sm layui-btn-<?php echo $vo['showps2']==1 ? 'warm' : 'black'; ?>" data-confirm="确定要显示吗?" data-action="<?php echo url('showps2'); ?>" data-value="id#<?php echo htmlentities($vo['id']); ?>;do#<?php echo $vo['showps2']==1 ? 0 : 1; ?>" data-csrf="<?php echo systoken('showps2'); ?>"><?php echo $vo['showps2']==1 ? "隐亏" : "显亏"; ?></a></div></td></tr><?php endforeach; ?></tbody></table><?php if(empty($list) || (($list instanceof \think\Collection || $list instanceof \think\Paginator ) && $list->isEmpty())): ?><span class="notdata">没有记录哦</span><?php else: ?><?php echo (isset($pagehtml) && ($pagehtml !== '')?$pagehtml:''); ?><?php endif; ?></div></div></div>